startMove();
function startMove()
{

	ttext._x=722;
	TweenLite.to(ttext,5, {_x:0,ease:None.easeNone,onComplete:contMove});
}
function contMove()
{

	TweenLite.to(ttext, 5, {_x:-722,ease:None.easeNone,delay:1,onComplete:startMove});
}
function openPole()
{
	//_parent.pole._visible = true;
	_parent.attachMovie("vkladka","vkladka",maska.getDepth() + 2);
	_parent.attachMovie("pole","pole",maska.getDepth() + 3);
	_parent.pole._visible = false;
	_parent.pole._y=26;
	_parent.pole._x=1;
	_parent.vkladka._y = -134;
	_parent.vkladka._x = 0;
	_parent.vkladka.setMask(_parent.pole);
	TweenLite.to(_parent.vkladka, .5, {_y:26,ease:None.easeNone,onComplete:poleOpened});
}
function closePole()
{
	if(_parent._parent._parent._parent.pages_mov.ticker._y!=-265.05){TweenLite.to(_parent._parent._parent._parent.pages_mov.ticker,.5,{_y:-265.05,ease:None.easeNone});}

	//if(190<_parent.pole._height){if(_root.pages_mov.ticker._y!=-400){_root.pages_mov.ticker.varTween = new Tween(_root.pages_mov.ticker, "_y",Strong.easeIn, -265.05,-400, 0.5, true);}}
	_root.menu_mov._visible=true;
	_parent.pole._height=200.55;
	_parent.vkladka.swapDepths(_parent.getNextHighestDepth());
	_parent.vkladka.removeMovieClip();
	_parent.pole.swapDepths(_parent.getNextHighestDepth());
	_parent.pole.removeMovieClip();
	//trace(_parent.pole);
}
maska.onRollOver = function()
{
	if(_parent.vkladka._y<=-82)
	{
		TweenLite.to(ttext, 1, {_x:0,ease:None.easeNone,onComplete:openPole});
	}else{};
};
maska.onRollOut = function()
{
	if(_parent.vkladka._y<=-82)
	{
		TweenLite.to(_parent.vkladka, .5, {_y:-138,ease:None.easeNone,onComplete:closePole});
		if(ttext._x==0)
		{
			TweenLite.to(ttext, 5, {_x:-722,ease:None.easeNone,onComplete:startMove});
			
		}else{var t=(722+ttext._x)/144.4
			TweenLite.to(ttext, t, {_x:-722,ease:None.easeNone,onComplete:startMove});}
	}else{};
};

function poleOpened()
{
	maska.onRollOut = function(){};
	/*var chk:Number = setInterval (checkOver, 5);
	function checkOver () */
	onEnterFrame=function()
	{
		var wasOver1:Boolean=false;
		var wasOver2:Boolean=false;
		var hitTest1:Boolean = maska.hitTest(_root._xmouse, _root._ymouse);
		var hitTest2:Boolean = _parent.vkladka.hitTest(_root._xmouse, _root._ymouse);
		if (!wasOver1) {if (hitTest1) {wasOver1 = true;}}
		else {if (!hitTest1) {wasOver1 = false;}}
		if (!wasOver2) {if (hitTest2) {wasOver2 = true;}}
		else {if (!hitTest2) {wasOver2 = false;}}
		if(wasOver1==false && wasOver2==false)
		{
			TweenLite.to(_parent.vkladka, .5, {_y:-138,ease:None.easeNone,onComplete:closePole});
			TweenLite.to(ttext, 5, {_x:-722,ease:None.easeNone,onComplete:startMove});
			onEnterFrame=function(){};
		}
	};
}



















